package com.dztalk.activities;

import android.content.Intent;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.net.Uri;
import android.os.Bundle;
import android.provider.MediaStore;
import android.util.Base64;
import android.util.Patterns;
import android.view.View;
import android.widget.Toast;

import androidx.activity.result.ActivityResultLauncher;
import androidx.activity.result.contract.ActivityResultContracts;
import androidx.appcompat.app.AppCompatActivity;

import com.dztalk.databinding.ActivityEnrollBinding;
import com.dztalk.models.UserProperties;
import com.google.firebase.firestore.FirebaseFirestore;

import java.io.ByteArrayOutputStream;
import java.io.FileNotFoundException;
import java.io.InputStream;
import java.util.HashMap;

public class EnrollActivity extends AppCompatActivity {

    private static final String DEFAULT_PROFILE = "";

    private ActivityEnrollBinding binding;
    private String enrollProfile = DEFAULT_PROFILE;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        binding = ActivityEnrollBinding.inflate(getLayoutInflater());
        setContentView(binding.getRoot());
        // listener of click
        listener();
    }

    /**
     * Listener of click
     */
    private void listener(){
        // listen the 'Login Here' button
        binding.loginHere.setOnClickListener(v -> onBackPressed());
        // listen the 'Register' button
        binding.buttonRegister.setOnClickListener(v -> {
            // add new user to db after checking the input
            if (isValidRegisterCheck()) addNewUserToDB();
        });
        binding.layoutProfile.setOnClickListener(v -> {
            Intent intent = new Intent(Intent.ACTION_PICK, MediaStore.Images.Media.EXTERNAL_CONTENT_URI);
            intent.addFlags(Intent.FLAG_GRANT_PERSISTABLE_URI_PERMISSION);
            selectImage.launch(intent);
        });
    }


    /**
     * Encode the image to store it
     * @return
     */
    private String encodeImage(Bitmap bitmap){
        int width = 150;
        int height = bitmap.getHeight() * width / bitmap.getWidth();
        Bitmap previewBitmap = Bitmap.createScaledBitmap(bitmap, width, height, false);
        ByteArrayOutputStream byteArrayOutputStream = new ByteArrayOutputStream();
        previewBitmap.compress(Bitmap.CompressFormat.JPEG, 50, byteArrayOutputStream);
        byte[] bytes = byteArrayOutputStream.toByteArray();
        return Base64.encodeToString(bytes, Base64.DEFAULT);
    }

    /**
     * Let the user to pick image
     */
    private final ActivityResultLauncher<Intent> selectImage = registerForActivityResult(
            new ActivityResultContracts.StartActivityForResult(),
            result -> {
                if (result.getResultCode() == RESULT_OK){
                    if (result.getData() != null){
                        Uri imageUri = result.getData().getData();
                        try{
                            InputStream inputStream = getContentResolver().openInputStream(imageUri);
                            Bitmap bitmap = BitmapFactory.decodeStream(inputStream);
                            binding.enrollProfile.setImageBitmap(bitmap);
                            enrollProfile = encodeImage(bitmap);
                        }catch (FileNotFoundException e){
                            showToast(e.toString());
                        }
                    }
                }
            }
    );

    /**
     * Add new user to the firebase
     */
    private void addNewUserToDB(){
        // loading
        loadingPage(true);

        // initialize the db
        FirebaseFirestore db = FirebaseFirestore.getInstance();

        // get the information
        HashMap<String, Object> newUserInfo = new HashMap<>();

        // get message
        newUserInfo.put(UserProperties.KEY_USER_Profile, enrollProfile);
        newUserInfo.put(UserProperties.KEY_FIRSTNAME, binding.enrollInputFirstName.getText().toString());
        newUserInfo.put(UserProperties.KEY_LASTNAME, binding.enrollInputLastName.getText().toString());
        newUserInfo.put(UserProperties.KEY_FULLNAME, binding.enrollInputFirstName.getText().toString() + " " + binding.enrollInputLastName.getText().toString());
        newUserInfo.put(UserProperties.KEY_GENDER, binding.enrollInputGender.getText().toString());
        newUserInfo.put(UserProperties.KEY_EMAIL, binding.enrollInputEmail.getText().toString());
        newUserInfo.put(UserProperties.KEY_PASSWORD, binding.enrollInputPassword.getText().toString());
        newUserInfo.put(UserProperties.KEY_LOCATION, "Unknown");
        newUserInfo.put(UserProperties.KEY_STATUS, "status");
        newUserInfo.put(UserProperties.KEY_BIRTHDAY, "Unknown");
        newUserInfo.put(UserProperties.KEY_HOMETOWN, "Unknown");
        newUserInfo.put(UserProperties.KEY_JOB, "Unknown");
        newUserInfo.put(UserProperties.KEY_DESCRIPTION, "Unknown");
        newUserInfo.put(UserProperties.KEY_CONTACT, new HashMap<String, String>());
        newUserInfo.put(UserProperties.KEY_REQUEST, new HashMap<String, String>());

        // check the email is unique
        db.collection(UserProperties.USER_DB_NAME)
                .whereEqualTo(UserProperties.KEY_EMAIL, binding.enrollInputEmail.getText().toString())
                .get()
                .addOnCompleteListener(task -> {
                    if (task.isSuccessful() && task.getResult() != null
                    && task.getResult().getDocuments().size() > 0){
                        showToast("The email has been used. Please use another one.");
                        loadingPage(false);
                    }
                    // store data to db
                    else{
                        db.collection(UserProperties.USER_DB_NAME)
                                .add(newUserInfo)
                                .addOnSuccessListener(documentReference -> {
                                    Toast.makeText(getApplicationContext(),
                                            "Enroll Successfully! Go to login", Toast.LENGTH_SHORT).show();
                                })
                                .addOnFailureListener(exception -> {
                                    Toast.makeText(getApplicationContext(),
                                            exception.getMessage(), Toast.LENGTH_SHORT).show();
                                });
                        loadingPage(false);
                    }
                });
    }

    /**
     * Show error message to user through toast
     */
    private void showToast(String message){
        Toast.makeText(getApplicationContext(), message, Toast.LENGTH_SHORT).show();
    }

    /**
     * Check whether the register is legal
     * @return
     */
    private Boolean isValidRegisterCheck(){
        // check all the blanks are filled up
        if (enrollProfile == null){
            showToast("Profile missing");
            return false;
        }else if (binding.enrollInputFirstName.getText().toString().trim().isEmpty()){
            showToast("'First Name' missing");
            return false;
        }else if (binding.enrollInputLastName.getText().toString().trim().isEmpty()){
            showToast("'Last Name' missing");
            return false;
        }else if (binding.enrollInputEmail.getText().toString().trim().isEmpty()){
            showToast("'Email' missing");
            return false;
        } else if (!Patterns.EMAIL_ADDRESS.matcher(binding.enrollInputEmail.getText().toString()).matches()){
            showToast("Please enter valid email");
            return false;
        }else if (binding.enrollInputGender.getText().toString().trim().isEmpty()){
            showToast("'Gender' missing");
        }else if (binding.enrollInputPassword.getText().toString().trim().isEmpty()){
            showToast("'Password' missing");
            return false;
        }

        return true;
    }

    private void loadingPage(Boolean isLoading){
        if (isLoading){
            binding.buttonRegister.setVisibility(View.INVISIBLE);
            binding.loadingBar.setVisibility(View.VISIBLE);
        }else{
            binding.buttonRegister.setVisibility(View.VISIBLE);
            binding.loadingBar.setVisibility(View.INVISIBLE);
        }
    }
}